class ResolvePromise extends AsyncDirective {
    render(promise) {
        Promise.resolve(promise).then(resolvedValue => {
            this.setValue(resolvedValue);
        });
        return noChange;
    }
}
const resolvePromise = directive(ResolvePromise);

const InstanceType = {
    'SWIMLANE': 'swimlane',
    'TURBINE': 'turbine',
    'SHARED_CLOUD': 'shared_cloud'
}

class SwimlaneApi {

    bearerToken = null;
    swimlane_api_url = null;
    user_api_url = null;
    instanceType = null;

    constructor(contextData) {

        let swimlane_api_root = "/api";
        let orchestration_api_root = "/orchestration/api"; // removing v1 here because playbook related resources are spread across v1 and v2 as of Version 23.3.0

        if (contextData.application.buttons) { // some flavor of Turbine
            if (contextData.tenantPath) { // then this is Turbine Shared Cloud
                this.instanceType = InstanceType.SHARED_CLOUD;
                let multitenant_substring = '/api/'.concat(contextData.tenantPath);
                swimlane_api_root = swimlane_api_root.replace('/api', multitenant_substring);
                orchestration_api_root = orchestration_api_root.replace('/api', multitenant_substring);
                this.user_api_url = contextData.origin.concat('/tenant/api/tenants/', contextData.tenantId, '/users');
            } else {
                this.instanceType = InstanceType.TURBINE;
                this.bearerToken = contextData.token;
            }
        } else {
            this.instanceType = InstanceType.SWIMLANE;
            this.bearerToken = contextData.token;
        }

        this.swimlane_api_url = contextData.origin.concat(swimlane_api_root);
        this.orchestration_api_url = contextData.origin.concat(orchestration_api_root);
    }

    // SIMPLE GET, PUT, & POST
    async GET(resource, objectId = '', orchestrationVersion = '') {
        let completeResource = '';
        if (orchestrationVersion.length > 0) {
            completeResource = `${this.orchestration_api_url}/${orchestrationVersion}/${resource}/${objectId}`;
        }
        else {
            completeResource = `${this.swimlane_api_url}/${resource}/${objectId}`;
        }
        let options = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        }
        if (this.bearerToken) {
            options.headers.Authorization = 'Bearer '.concat(this.bearerToken);
        }
        return this.fetchWrap(completeResource, options)
    }

    async fetchWrap(resource, options) {

        try {
            return await fetch(resource, options);
        } catch (error) {
            let completeMessage = `URL: ${resource};\nMETHOD: ${options.method};\n${error}`;
            console.log(completeMessage);
        }
    }
}

export default class extends SwimlaneElement {

    swimlaneApi = null;
    checklist_items = null;
    expanded_items = [];

    static get styles() {
        return [super.styles,
            css`
            .widget-container{
                display: block;
                background-color: #212631;
                font-family: 'Source Sans Pro', 'Open Sans', Arial, sans-serif;
                border-bottom: 3px solid #313847;
                margin-bottom: 25px;
            }
            .steps-container {
                display: flex;
                align-items: center;
                gap: 8px;
                padding: 8px;
                overflow: hidden;
                background: #171a21;
                overflow-x: scroll;
                scrollbar-color: #373F50 #1C1E27;
                scrollbar-width: thin;
            }
            `
        ];
    }

    constructor() {

        super();
    }

    render() {

        if (!this.contextData) {

            console.log('No contextData');
        }
        else {

            if (!this.swimlaneApi) {

                this.swimlaneApi = new SwimlaneApi(this.contextData);
            }
            return resolvePromise(this.template());
        }
    }

    async initialize() {

        console.log('initialize')

        if (this.initialized) return;

        this.initialized = true;

        await this.loadChecklistDefinition();

        console.log(`checklist_field_definition : ${this.checklist_field_definition}`)
    }

    async loadApplicationFieldDefinitions() {

        console.log('loadApplicationFieldDefinitions')

        let application_response = await this.swimlaneApi.GET('app', this.contextData.application.id);

        let application_definition = await application_response.json();

        this.app_field_defs = application_definition.fields;
    }

    async getFieldDefinition(fieldKey) {

        console.log('getFieldDefinition')

        if (!this.app_field_defs) {

            await this.loadApplicationFieldDefinitions();
        }

        let field_obj = this.app_field_defs.find((obj) => obj.key === fieldKey);

        if (!field_obj) {

            throw new Error(`A field with the key "${fieldKey}" was not found in this Application.`);
        }
        return field_obj;
    }

    async loadChecklistDefinition() {

        console.log('loadChecklistDefinition')

        let field_obj = await this.getFieldDefinition(CHECKLIST_FIELD_KEY);

        //console.log(`field_obj : ${JSON.stringify(field_obj,null,4)}`)

        this.checklist_field_definition = field_obj.values.map((obj) => {
            return {
                id: obj.id,
                name: obj.name,
                description: obj.description
            }
        });

        //console.log(`checklist_field_definition : ${JSON.stringify(this.checklist_field_definition,null,4)}`)
    }

    async loadRecordValues() {

        console.log('loadRecordValues')

        if(!this.checklist_field_definition){

            console.log('01')

            await this.loadChecklistDefinition();
        }

        if (!this.record[FOCUS_FIELD]) {

            this.focus_step = this.checklist_field_definition[0].name
        }
        else {

            this.focus_step = this.record[FOCUS_FIELD]
        }

        this.checklist_items = this.checklist_field_definition.map((obj, idx) => {

            let checked = false;

            if (this.record[CHECKLIST_FIELD_KEY]) {

                checked = this.record[CHECKLIST_FIELD_KEY].find((element) => element.id === obj.id) ?
                    true : false;
            }

            let selected = obj.name === this.focus_step ? true : false;

            return {
                id: obj.id,
                stepNumber: idx + 1,
                name: obj.name,
                description: obj.description,
                checked: checked,
                selected: selected
            }
        })
    }

    async metaTemplate() {

        /**
         * This is be a reflection of whatever consts in widget config are
         * pointing to a field.
        */

        await this.initialize();

        return html`
        <pre>

        </pre>
        `
    }

    async template() {

        console.log('template')

        if (!this.contextData || !this.record) return

        if (!this.initialized) {

            await this.initialize();
        }

        await this.loadRecordValues();

        return html`
        <div class="widget-container">
            <div class="steps-container">
                ${this.checklist_items.map(
            (checklist_item) => html`
                        <process-step
                            .id="${checklist_item.id}"
                            .stepNumber="${checklist_item.stepNumber}"
                            .name="${checklist_item.name}"
                            .description="${checklist_item.description}"
                            .selected="${checklist_item.selected}"
                            .checked="${checklist_item.checked}"
                            @step-selected="${this.stepSelected}"
                        ></process-step>`
        )}
            </div>
            <div class="checklist-container">
                ${this.checklist_items.map(
            (checklist_item) => html`
                        <widget-checkbox
                            .id="${checklist_item.id}"
                            .name="${checklist_item.name}"
                            .description="${checklist_item.description}"
                            .selected="${checklist_item.selected}"
                            .checked="${checklist_item.checked}"
                            @checkbox-changed="${this.checkboxChanged}"
                        ></widget-checkbox>`
        )}
            </div>
        </div>
        `;
    }

    // HANDLERS ===============================================================

    async stepSelected(e) {

        let selectedStep = this.record[FOCUS_FIELD]

        if (selectedStep && selectedStep === e.detail.name) {

            // cool
        }
        else {

            await this.updateRecordValue(FOCUS_FIELD, e.detail.name);

            await this.requestUpdate();
        }
    }

    async checkboxChanged(e) {

        let currentChecklistSelections = this.record[CHECKLIST_FIELD_KEY] ? this.record[CHECKLIST_FIELD_KEY] : []

        let checkedStep = currentChecklistSelections.find((element) => element.id === e.detail.id);

        if (checkedStep) {
            // remove it
            currentChecklistSelections = currentChecklistSelections.filter((element) => element.id !== e.detail.id)
        }
        else {
            let addlSelection = this.checklist_items.find((element) => element.id === e.detail.id);

            currentChecklistSelections.push({
                value: addlSelection.name
            })
        }

        console.log(`currentChecklistSelections : ${JSON.stringify(currentChecklistSelections,null,4)}`)

        let checkedNames = currentChecklistSelections.map(checked => checked.value)

        console.log(`checkedNames : ${JSON.stringify(checkedNames,null,4)}`)

        await this.updateRecordValue(CHECKLIST_FIELD_KEY, checkedNames)

        await this.triggerSave();

    }
}

