export class Checkbox extends SwimlaneElement {

    static get properties() {
        return {
            id: { type: String },
            name: { type: String },
            description: { type: String },
            selected: { type: Boolean },
            checked: { type: Boolean }
        }
    };

    constructor() {
        super();
        this.id = null;
        this.name = '';
        this.description = '';
        this.selected = false;
        this.checked = false;
    }

    static get styles() {
        return [super.styles,
        css`
            .container {
                display: none;
                min-height: 110px;
                margin-top: 15px;
            }
            .selected {
                display: block;
            }
            .title {
                font-weight: 700;
            }
            label {
                font-size: 16px;
            }
            p {
                font-weight: 400;
                font-size: 14px;
            }
        `];
    }

    render() {
        let selected = this.selected ? 'selected' : ''
        return html`
            <div class="container ${selected}">
                <label class="title">${this.name}</label><br>
                ${unsafeHTML(this.description)}
                <input type="checkbox"
                    @change="${this.handleCheckboxChange}"
                    id="${this.id}"
                    ?checked="${this.checked}"></input>
                <label>Mark as complete</label><br><br>
            </div>
        `;
    }

    handleCheckboxChange(e) {

        this.checked = e.target.checked;
        this.dispatchEvent(new CustomEvent('checkbox-changed', {
            detail: { id: this.id, checked: this.checked },
            bubbles: true,
            composed: true
        }));
    }
}

if (customElements.get('widget-checkbox') === undefined) {
    customElements.define('widget-checkbox', Checkbox);
}

