    </g>
    <defs>
    <clipPath id="clip0_1071_1">
    <rect width="32" height="32" fill="#1DDEB6"/>
    </clipPath>
    </defs>
    </svg>
        `;

    render() {

        let selected = this.selected ? 'selected' :
            this.checked ? '' : 'inactive-incomplete';

        let descText = this.selected ? '' :
            this.checked ? 'inactive-complete-description' : '';

        return html`
            <section class="container ${selected}"
                @click="${(e) => this.handleStepSelected(e)}">
                ${this.topElement()}
                <label class="title">${this.name}</label><br>
                <span class="description ${descText}">
                ${unsafeHTML(this.description)}
                </span>
            </section>
        `;
    }

    topElement() {

        let selected = this.selected ? '' : 'inactive-number';

        if (this.checked) {
            return html`
            <div class="phase-circle">${this.checkMarkIcon}</div>
            `
        } else {
            return html`
            <div class="number-box ${selected}">${this.stepNumber}</div>
            `
        }

    }

    handleStepSelected(e) {

        this.dispatchEvent(new CustomEvent('step-selected', {
            detail: { name: this.name, selected: true },
            bubbles: true,
            composed: true
        }));
    }
}

if (customElements.get('process-step') === undefined) {
    customElements.define('process-step', ProcessStep);
}

export class Checkbox extends SwimlaneElement {

    static get properties() {
        return {
            id: { type: String },
            name: { type: String },
            description: { type: String },
            selected: { type: Boolean },
            checked: { type: Boolean }
        }
    };

    constructor() {
        super();
        this.id = null;
        this.name = '';
        this.description = '';
        this.selected = false;
        this.checked = false;
    }

    static get styles() {
        return [super.styles,
        css`
            .container {
                display: none;
                min-height: 110px;
                margin-top: 15px;
            }
            .selected {
                display: block;
            }
            .title {
                font-weight: 700;
            }
            label {
                font-size: 16px;
            }
            p {
                font-weight: 400;
                font-size: 14px;
            }
        `];
    }

    render() {
        let selected = this.selected ? 'selected' : ''
        return html`
            <div class="container ${selected}">
                <label class="title">${this.name}</label><br>
                ${unsafeHTML(this.description)}
                <input type="checkbox"
                    @change="${this.handleCheckboxChange}"
                    id="${this.id}"
                    ?checked="${this.checked}"></input>
                <label>Mark as complete</label><br><br>
            </div>
        `;
    }

    handleCheckboxChange(e) {

        this.checked = e.target.checked;
        this.dispatchEvent(new CustomEvent('checkbox-changed', {
            detail: { id: this.id, checked: this.checked },
            bubbles: true,
            composed: true
        }));
    }
}

if (customElements.get('widget-checkbox') === undefined) {
    customElements.define('widget-checkbox', Checkbox);
}

